import React from 'react';
import UserService from "../../service/UserService";
import {User} from '../../model/entity/User';
import './form_page.css';
import '../../common/app.css';
import ModalMessage from "../modal/modal_message";

const accessTokenName = "access-token";

export default class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        if(localStorage.getItem(accessTokenName)) {
            // TODO
        }

        this.state = {
            user: new User(),
            submitted: false,
            loading: false,
            errorMessage: null
        };
    }

    handleInput(e) {
        const {name, value} = e.target;
        const user = this.state.user;

        user[name] = value;

        this.setState({user: user});
    }

    handleLogin(e) {
        this.setState({submitted: true});

        let{user} = this.state;

        if(!(user["email"] && user["password"])) {
            return;
        }

        this.setState({loading: true});

        UserService.login(user).then(response => {
            localStorage.setItem(accessTokenName, response.headers["access_token"]);
            localStorage.setItem("user", JSON.stringify(response.data));

            user = response.data;

            console.info("Giriş başarılı: "+user.name.firstName + " " + user.name.lastName + "\nYetki: "+user.role);

            this.setState({errorMessage: null, loading: false});
            // window.location = "/home";
        }).catch(error => {
            this.setState({
                errorMessage: error.response.data,
                loading: false
            })
        });
    }

    render() {
        const{user, submitted, loading, errorMessage, hasError} = this.state;
        
        return(
            <div className="container">
                { this.props.submitted &&
                    <ModalMessage id="plainMessage" message="Başarılı bir şekilde hesap oluşturdunuz. Lütfen giriş yapınız" subtitle="Hesap oluşturma başarılı" title="Bilgi" type="info" />
                }
                <div className="container form-container">
                    <div className="form center-form">
                        <h1 className="big-title"><strong>Giriş</strong></h1>
                        <div className="form-control-group">
                            <div className="form-control">
                                <label>Email</label>
                                <input type="text" name="email" className="input text-input" value={user.email} placeholder="E-posta adresiniz" id="emailField" onChange={(e) => this.handleInput(e)}/>
                                {submitted && !user.email &&
                                <p className="alert danger">E-posta adresinizi giriniz</p>
                                }
                            </div>
                            <div className="form-control">
                                <label>Şifre</label>
                                <input type="password" name="password" className="input text-input" value={user.password} placeholder="Şifreniz" id="passwordField" onChange={(e) => this.handleInput(e)}/>
                                {submitted && !user.password &&
                                <p className="alert danger">Şifrenizi giriniz</p>
                                }
                            </div>
                            { errorMessage &&
                            <div className="form-control">
                                <p className="alert danger">{errorMessage}</p>
                            </div>
                            }
                            <div className="form-control">
                                <button disabled={loading} onClick={(e) => this.handleLogin(e)} className="action-element">Giriş</button>
                            </div>
                            <div className="form-control">
                                <a className="link standard-link" href="/signup">Yeni bir hesap oluşturun</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
