import React from "react";

export default class ModalMessage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            message: this.props.message,
            type: this.props.type,
            id: this.props.id,
            title: this.props.title,
            subtitle: this.props.subtitle
        }
    }

    dismiss(e) {
        if(e.target === document.getElementById(this.state.id) || e.target === document.getElementById("modalDismissButton")) {
            document.getElementById(this.state.id).remove();
        }
    }

    render() {
        const{message, type, title, id, subtitle} = this.state;

        return (
            <div className="modal modal-background" id={id} onClick={(e) => this.dismiss(e)}>
                <div className="center-container">
                    <div className="modal-header">
                        <h2 className="modal-title">{title}</h2>
                        <p className="modal-dismiss-button">X</p>
                    </div>
                    <hr/>
                    <div className="modal-body">
                        <p className={"alert modal-alert " + type}>{subtitle}</p>
                        <p className="message">{message}</p>
                    </div>
                    <div className="modal-footer right-align">
                        <button className="standart-button" id="modalDismissButton" onClick={(e) => this.dismiss(e)}>Dismiss</button>
                    </div>
                </div>
            </div>
        );
    }
}
