import React from "react";
import Dropdown from "./dropdown";

export default class PageHeader extends React.Component {
    constructor(props) {
        super(props);

        const userMenuContent = [
            {
                text: "Profil",
                href: "/profile",
                key: "profile"
            },
            {
                text: "Ayarlar",
                href: "/settings",
                key: "settings"
            },
            {
                text: "Çıkış",
                href: "/login?logout",
                key: "logout"
            },
            {
                text: "Hakkında",
                href: "/about",
                key: "about"
            }
        ];

        const redirect = this.redirect;

        const contents = userMenuContent.map((obj) =>
            <div className="dropdown-option flex" onClick={redirect(obj)}>
                <p key={obj.key} href={obj.href} className="dropdown-option-text">{obj.text}</p>
            </div>
        );

        this.state = {
            userMenuContent:  contents
        };
