import React from "react";
import '../../common/app.css';
import './home_page.css';
import {User} from "../../model/entity/User";
import {Book} from "../../model/entity/Book";
import PageHeader from "../fragment/PageHeader";
import BookService from "../../service/BookService";
import Info from "../fragment/Info";

export default class HomePage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            accessToken: localStorage.getItem("access-token"),
            refreshToken: localStorage.getItem("refresh-token"),
            loading: true,
        }
    }

    componentDidMount() {
        this.setState({loading: true});

        BookService.getBookList().then(response => {
            console.log(response.data);
            this.setState({
                bookList: response.data,
                loading: false,
            });
        }).catch(err => {
            console.log(err);
        });

    }

    render() {
        const{bookList, loading} = this.state;
        let list;
        if(bookList) {
            console.log("booklist vra: "+JSON.stringify(bookList));
            list = bookList.map((book, index) => 
            <div className="flex space-between list-row">
                <h2 className="flex-center-align">{index+1}</h2>
                <img className="book-img flex-center-align" src={book.imageUrl}/>
                <h2 className="medium-title flex-center-align">{book.title}</h2>
                <h2 className="medium-title flex-center-align">{book.price + " ₺"}</h2>
            </div>
            );
        }

        return (
            <div className="container">
                {<PageHeader/>}
                <div className="page-body">
                { !loading &&
                    <Info message="Loading..."/>
                }
                { bookList &&
                    list
                }
                </div>
                <div className="page-footer">

                </div>
            </div>
        );
    }
}
