import React from "react";
import Dropdown from "./dropdown";

export default class PageHeader extends React.Component {
    constructor(props) {
        super(props);

        const userMenuContent = [
            "Profil",
            "Ayarlar",
            "Çıkış"
        ]

        let i = 0;
        const contents = this.state.userMenuContent.map((str, index) =>
            <div className="dropdown-option">
                <p key={index} className="dropdown-option-text">{str}</p>
            </div>
        );

        this.state = {
            userMenuContent:  contents
        };
    }

    render() {
        const user = JSON.parse(localStorage.getItem("user"));
        const{userMenuContent} = this.state

        return(
            <div className="page-header flex space-between">
                <h2 className="big-title secondary">Bookstore</h2>
                <div className="flex flex-center-align space-between">
                    <input placeholder="ISBN" className="flex-center-align" type="text" id="search"/>
                    <button className="standard-button width-fit-content flex-center-align no-margin">Ara</button>
                </div>
                {<Dropdown title={user.username} id="userMenu" content={userMenuContent}/>}
            </div>
        );
    }

}
