import React from "react";
import '../../common/app.css';
import './home_page.css';
import {User} from "../../model/entity/User";

export default class HomePage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            user: new User(),
            accessToken: localStorage.getItem("access-token"),
            refreshToken: localStorage.getItem("refresh-token")
        }
    }

    componentDidMount() {
        this.setState({user: JSON.parse(localStorage.getItem("user"))});
    }

    render() {
        const{user} = this.state;

        return(
            <div className="container">
                <div className="page-header flex space-between">
                    <h2 className="big-title secondary">Bookstore</h2>
                    <div className="flex flex-center-align space-between">
                        <label className="secondary flex-center-align" htmlFor="search">Ara:</label>
                        <input type="text" id="search"/>
                    </div>
                    <div className="flex dropdown flex-center-align secondary">Seçenekler</div>
                </div>
                <div className="page-body orange">
                    <label>İsim: </label>{user.name.firstname + " " + user.name.lastname} <br/> <br/>
                    <label>Kullanıcı adı: </label> {user.username} <br/> <br/>
                    <label>Email: </label> {user.email} <br/> <br/>
                    <label>Süre: </label> {new Date(user.accountCreationDate).getDate()} <br/> <br/>
                </div>
                <div className="page-footer">
                    footer text
                </div>
            </div>
        );
    }
}
