import React from 'react';
import UserService from "../../service/UserService";
import {User} from '../../model/entity/User';
import './login_page.css';

const accessTokenName = "access-token";

export default class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        if(localStorage.getItem(accessTokenName)) {
            // TODO
        }

        this.state = {
            user: new User(),
            submitted: false,
            loading: false,
            errorMessage: 'Error'
        };
    }

    handleChange(e) {
        var{name, value} = e.target;
        var user = this.state.user;

        user[name] = value;

        this.setState({user: user});
    }

    handleLogin(e) {
        console.log("a");
        this.setState({submitted: true});

        const{user} = this.state;

        alert("username: "+user["username"]+"\npassword: "+user["password"]);

        if(!(user["username"] && user["password"])) {
            return;
        }



        this.setState({loading: true});

        UserService.login(user).then(data => {
           localStorage.setItem("access-token", data.headers["access_token"]);
           window.location = "/home";
           this.setState({loading: false});
        }, error => {
            this.setState({
                errorMessage: "Username or password is not valid",
                loading: false
            })
        });
    }

    test(e) {
        UserService.test("a");
    }

    render() {
        const{user, submitted, loading, errorMessage} = this.state;

        return(
            <form aria-disabled={loading} onSubmit={(e) => this.handleLogin(e)} className={"form box"}>
                <h1 className={"big-title form-header"}><strong>Login</strong></h1>
                { errorMessage &&
                <div className={"alert danger"}>
                    <h3>{errorMessage}</h3>
                </div>
                }
                <div className={"form-control-group"}>
                    <div className={"form-control"}>
                        <label htmlFor={"usernameField"}><strong>Username:</strong></label>
                        <input type="text" name={"username"} className={"input text-input"} value={user["username"]} placeholder={"username"} id={"usernameField"}/>
                    </div>
                    <div className={"form-control"}>
                        <label htmlFor={"passwordField"}><strong>Password:</strong></label>
                        <input type="password" name={"password"} className={"input text-input"} value={user["password"]} placeholder={"password"} id={"passwordField"}/>
                    </div>
                    <div className={"form-control"}>
                        <input type={"submit"} className={"button standart-button"}>Login</input>
                    </div>
                </div>
            </form>
        )
    }
}
