import React from "react";
import '../../common/app.css';

export default class Dropdown extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            display: false,
            content: [],
        }
    }

    show() {
        this.setState({display: true});
    }

    dismiss() {
        this.setState({display: false});
    }

    render() {
        const user = JSON.parse(localStorage.getItem("user"));
        const {display, content} = this.state;

        const contents = content.map((str) =>
            <div className="dropdown-option">
                <p className="dropdown-option-text">{str}</p>
            </div>
        );

        return(
            <div className={"flex dropdown flex-center-align secondary" + (display ? " display-block" : " display-none")}>
                <p className="dropdown-title">
                    {user.username}
                </p>
                <div className="dropdown-content-container width-fit-content">
                    <div className="dropdown-option-container">
                        {contents}
                    </div>
                </div>
            </div>
        );
    }
}
