import React from "react";
import '../../common/app.css';

export default class Dropdown extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            display: false,
            content: props.display,
            title: props.title,
            id: props.id
        }
    }

    toggleDisplay(e) {
        const{display, id} = this.state;

        if(e.target === document.getElementById(id)) {
            if(display) this.setState({display: false});
            else this.setState({display: true});
        }
    }

    render() {
        const {display, content, title, id} = this.state;

        const contents = content.map((str) =>
            <div className="dropdown-option">
                <p className="dropdown-option-text">{str}</p>
            </div>
        );

        return(
            <div className={"flex dropdown flex-center-align secondary" + (display ? " display-block" : " display-none")} id={id} onClick={(e) => this.toggleDisplay(e)}>
                <p className="dropdown-title">
                    {title}
                </p>
                <div className="dropdown-content-container width-fit-content">
                    <div className="dropdown-option-container">
                        {contents}
                    </div>
                </div>
            </div>
        );
    }
}
