import axios from 'axios'
import {BehaviorSubject} from 'rxjs';

const URL = "http://localhost:8765/api/user";
const currentUser = new BehaviorSubject(JSON.parse(localStorage.getItem('currentUser')));

class UserService {
  get currentUserValue() {
    return currentUser.value;
  }

  get currentUser() {
    return currentUser.asObservable;
  }

  login(user) {
    const headers = {
      "Content-Type": "application/json",
      "Access-Control-Allow-Origin": "*"
    }

    return axios({
      url: URL + "/login",
      method: "POST",
      withCredentials: false,
      data: JSON.stringify(user)
    });
  }

  updateLastLogin(loginResponse) {
    const body = {
      id: loginResponse.data.user.userId,
      date: loginResponse.data.user.lastLoginDate
    }

    const headers = {
      "Content-Type": "application/json",
      "Access-Control-Allow-Origin": "*",
      "Authorization": "Bearer " + loginResponse.headers["access_token"]
    }

    axios({
      url: URL + "/checkin",
      headers: headers,
      method: "POST",
      withCredentials: false,
      data: JSON.stringify(new Date())
    }).then(response => {
      console.log("login success")
    }, error => {
      console.log(error);
    });
  }

  logout() {
    return axios.post(URL + "/logout", {}).then(response => {
      localStorage.removeItem('currentUser');
      currentUser.next(null);
    });
  }

  register(user) {
    return axios.post(URL + 'register', JSON.stringify(user)), {headers: {'Content-Type':'application/json; charset=UTF-8'}};
  }
}

export default new UserService();
