import React from 'react';
import UserService from "../../service/UserService";
import {User} from '../../model/entity/User';
import './login_page.css';
import data from "bootstrap/js/src/dom/data";

const accessTokenName = "access-token";

export default class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        if(localStorage.getItem(accessTokenName)) {
            // TODO
        }

        this.state = {
            user: new User(),
            submitted: false,
            loading: false,
            errorMessage: null,
        };
    }

    handleChange(e) {
        const {name, value} = e.target;
        const user = this.state.user;

        user[name] = value;

        this.setState({user: user});
    }

    handleLogin(e) {
        this.setState({submitted: true});

        const{user} = this.state;

        if(!(user["username"] && user["password"])) {
            return;
        }

        this.setState({loading: true});

        UserService.login(user).then(data => {
            localStorage.setItem("access-token", data.headers["access_token"]);
            alert("Giriş başarılı.");
            this.setState({errorMessage: null, loading: false});
            // window.location = "/home";
        }, error => {
            this.setState({
                errorMessage: error.response.headers["error"],
                loading: false
            })
        });
    }

    render() {
        const{user, submitted, loading, errorMessage, hasError} = this.state;

        return(
            <div className="container form-container">
                <div aria-disabled={loading} className="form center-form">
                    <h1 className="big-title form-header"><strong>Giriş</strong></h1>
                    <div className="form-control-group">
                        <div className="form-control">
                            <label>Kullanıcı adı</label>
                            <input type="text" name="username" className="input text-input" value={user.username} placeholder="kullanıcı adı" id="usernameField" onChange={(e) => this.handleChange(e)}/>
                            {submitted && !user.username &&
                            <p className="alert danger">Kullanıcı adınızı giriniz</p>
                            }
                        </div>
                        <div className="form-control">
                            <label>Şifre</label>
                            <input type="password" name="password" className="input text-input" value={user.password} placeholder="parola" id="passwordField" onChange={(e) => this.handleChange(e)}/>
                            {submitted && !user.password &&
                            <p className="alert danger">Şifrenizi giriniz</p>
                            }
                        </div>
                        { errorMessage &&
                        <div className="form-control">
                            <p className="alert danger">{errorMessage}</p>
                        </div>
                        }
                        <div className="form-control">
                            <button onClick={(e) => this.handleLogin(e)} className="button standart-button">Giriş</button>
                        </div>
                        <div className="form-control">
                            <a className="link standard-link" href="/signup">Yeni bir hesap oluşturun</a>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
