import React from 'react';
import UserService from "../../service/UserService";
import {User} from '../../model/entity/User';
import './login_page.css';

export default class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        if(UserService.currentUserValue) {
            this.props.history.push('/')
        }

        this.state = {
            user: new User('', ''),
            submitted: false,
            loading: false,
            errorMessage: ''
        };
    }

    handleChange(e) {
        var{name, value} = e.target;
        var user = this.state.user;

        user[name] = value;

        this.setState({user: user});
    }

    handleLogin(e) {
        e.preventDefault();

        this.setState({submitted: true});
        const{user} = this.state;

        if(!(user["username"] && user["password"])) {
            return;
        }

        this.setState({loading: true});

        UserService.login(user).then(data => {
           localStorage.setItem("access-token", data.headers["access_token"]);
        }, error => {
            this.setState({
                errorMessage: "Username or password is not valid",
                loading: false
            })
        });
    }

    test(e) {
        UserService.test("a");
    }

    render() {
        const{user, submitted, loading, errorMessage} = this.state;

        return(
            <div className="col-md-12">
                <div className="card card-container">
                    <img className="profile-img-card"  src="../../logo.svg"></img>
                    {errorMessage &&
                        <div className="alert alert-danger" role="alert">
                            <strong>Error !</strong> <br/> {errorMessage}
                        </div>
                    }
                    <form name="form" onSubmit={(e) => this.handleLogin(e)}>
                        <div className={'form-group' + (submitted && !user.username ? ' has-error': '')}>
                            <label htmlFor="username">Username</label>
                            <input type="text" className="form-control" name="username" value={user.username} onChange={(e) => this.handleChange(e)}></input>
                            {submitted && !user.username &&
                            <div className="help-block">Username is required</div>
                            }
                        </div>
                        <div className={'form-group' + (submitted && !user.password ? ' has-error': '')}>
                            <label htmlFor="password">Password</label>
                            <input type="text" className="form-control" name="password" value={user.password} onChange={(e) => this.handleChange(e)}></input>
                            {submitted && !user.password &&
                            <div className="help-block">Password is required</div>
                            }
                        </div>
                        <div className="form-group">
                            <button className="btn btn-lg btn-primary btn-block btn-signin form-submit-button" disabled={loading}>Login</button>
                        </div>
                        <div className="form-group">
                            <button className="btn" onClick={(e) => this.test(e)}>Test</button>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}
